import React, { useState, useEffect } from 'react';
import { VictoryChart, VictoryLine, VictoryTheme } from 'victory';

const BitcoinPriceGraph = () => {
  const [bitcoinPrices, setBitcoinPrices] = useState(null);

  useEffect(() => {
    const fetchBitcoinPrices = async () => {
      const response = await fetch(
        'https://api.coingecko.com/api/v3/coins/bitcoin/market_chart?vs_currency=gbp&days=14&interval=daily'
      );
      const data = await response.json();
      setBitcoinPrices(data.prices);
    };

    fetchBitcoinPrices();
  }, []);

  const transformData = (data) => {
    return data.map((price) => ({
      x: new Date(price[0]).toLocaleDateString(),
      y: price[1],
    }));
  };

  return (
    <div className='Graph'>
      <h1>Bitcoin Price Graph (GBP)</h1>
      {bitcoinPrices && (
        <VictoryChart theme={VictoryTheme.material}>
          <VictoryLine data={transformData(bitcoinPrices)} />
        </VictoryChart>
      )}
    </div>
  );
};

export default BitcoinPriceGraph;
