import React, { useState, useEffect } from 'react';
import { VictoryChart, VictoryLine, VictoryTheme } from 'victory';
import './App.css';

const BitcoinPriceGraph = ({ ticker }) => {
  const [bitcoinPrices, setBitcoinPrices] = useState(null);

  useEffect(() => {
    const fetchBitcoinPrices = async () => {
      const response = await fetch(
        `https://api.coingecko.com/api/v3/coins/${ticker}/market_chart?vs_currency=gbp&days=14&interval=daily`
      );
      const data = await response.json();
      setBitcoinPrices(data.prices);
    };

    fetchBitcoinPrices();
  }, [ticker]);

  const transformData = (data) => {
    return data.map((price) => ({
      x: new Date(price[0]).toLocaleDateString(),
      y: price[1],
    }));
  };

  return (
    <div>
      <h1>{`${ticker.toUpperCase()} Price Graph (GBP)`}</h1>
      {bitcoinPrices && (
        <VictoryChart
          theme={VictoryTheme.material}
          style={{
            axisLabel: { fontSize: 16 },
            tickLabels: { fontSize: 16 },
          }}
        >
          <VictoryAxis
            label="Date"
            style={{
              axisLabel: { padding: 35 },
            }}
          />
          <VictoryAxis
            dependentAxis
            label="Price (GBP)"
            style={{
              axisLabel: { padding: 40 },
            }}
          />
          <VictoryLine data={transformData(bitcoinPrices)} />
        </VictoryChart>
      )}
    </div>
  );
};

const TickerSearch = () => {
  const [ticker, setTicker] = useState('bitcoin');
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = () => {
    setTicker(searchTerm.toLowerCase());
  };
  return (

      <div className="App">
            <input
        type="text"
        placeholder="Enter a ticker symbol (e.g. bitcoin)"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
            {bitcoinPrices && (
              <VictoryChart theme={VictoryTheme.material} style = {{fontSize: 2}}width={1000} height={1000}>
                <VictoryLine data={transformData(bitcoinPrices)}style={{ data: {strokeWidth: 1} }} />
              </VictoryChart>
            )}
    </div>
  );
};

export default BitcoinPriceGraph;
