import React, { useState, useEffect } from 'react';
import { VictoryChart, VictoryLine, VictoryTheme } from 'victory';

const BitcoinPriceGraph = () => {
  const [bitcoinPrices, setBitcoinPrices] = useState(null);

  useEffect(() => {
    const fetchBitcoinPrices = async () => {
      const response = await fetch(
        'https://api.coingecko.com/api/v3/coins/ethereum/market_chart?vs_currency=gbp&days=14'
      );
      const data = await response.json();
      setBitcoinPrices(data.prices);
    };

    fetchBitcoinPrices();
  }, []);

  const transformData = (data) => {
    return data.map((price) => ({
      y: price[1],
    }));
  };

  return (

      <div className="App">
        <header className="App-header">
          <div>
            <h1>Price Graph (GBP)</h1>
            {bitcoinPrices && (
              <VictoryChart 
              theme={VictoryTheme.material} 
              width={200} height={200}
              style={{ 
                axisLabel: { fontSize: 5 },
                tickLabels: { fontSize: 5 }
              }}>
                <VictoryAxis 
            label="Date"
            style={{
              axisLabel: { padding: 35 }
            }}
          />
          <VictoryAxis 
            dependentAxis
            label="Price (GBP)"
            style={{
              axisLabel: { padding: 40 }
            }}
          />
                <VictoryLine data={transformData(bitcoinPrices)}style={{ data: { stroke: "#c43a31", strokeWidth: 0.5} }} />
              </VictoryChart>
            )}
          </div>
        </header>
    </div>
  );
};

export default BitcoinPriceGraph;
